import React,{useContext} from 'react'
import '../Pages/CSS/Shopcat.css'
//import all_product from '../Components/Assets/all_products'
import { ShopContext } from '../Context/ShopContext'
import dropdown_icon from './Components/Assets/dropdown_icon'
import { Items } from '../Components/Items/Items'


export const Shopcat = (props) => {
  const {all_product} = useContext(ShopContext)
  return (
    <div className="shop-category">
      <div className="shopcategory-indexSort">
        <p>
          <span>Showing 1-2
          </span>out of 10 products
        </p>
        <div className="shopcategory-sort">
          Sort by <img src= {dropdown-icon} alt=""/>

        </div>
      </div>
      <div className="shopcategory-products">
       {all_product.map((item,i)=>{
         if (props.category==item.category) {
          return <Items Items key = {i} id = {item.id} name= {item.name} image = {item.image} new_price = {item.new_price} old_price = {item.old_price}/>
         }
         else{
          return null

         }
       })}

      </div>

    </div>
  )
}
